import "./SideBar.css";
import logo from "../../assets/logo.png";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function SideBar() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));
  const [isEnrolled, setIsEnrolled] = useState(false);
  
  // useEffect(() => {
  //   const token = localStorage.getItem("token");
  //   if (!token) return;

  //   axios
  //     .get("http://localhost:3001/api/enroll/status", {
  //       headers: { Authorization: `Bearer ${token}` },
  //     })
  //     .then((res) => setIsEnrolled(res.data.enrolled))
  //     .catch(() => setIsEnrolled(false));
  // }, []);

  useEffect(() => {
    const handleStorageChange = () => {
      setIsLoggedIn(!!localStorage.getItem("token"));
    };

    window.addEventListener("authChange", handleStorageChange);

    return () => {
      window.removeEventListener("authChange", handleStorageChange);
    };
  }, []);

  const handleLoginClick = () => {
    navigate("/Login");
  };
  const handleRegisterClick = () => {
    navigate("/Register");
  };
  const handleEnroll = () => {
    navigate("/Enroll");
  };
  const handleDashboard = () => {
    navigate("/");
  };
  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    window.dispatchEvent(new Event("authChange"));
  };
  return (
    <div className="sidebar">
      <img src={logo} alt="Logo" className="logo-img" />
      <h2 className="logo">MyApp</h2>

      <ul className="menu">
        <li onClick={handleDashboard} className="dashboard-btn">
          Dashboard
        </li>
        <li>Profile</li>
        <li>Settings</li>
        {!isLoggedIn ? (
          <>
            <li onClick={handleLoginClick} className="login-btn">
              Login
            </li>
            <li onClick={handleRegisterClick} className="login-btn">
              Register
            </li>
          </>
        ) : (
          <>
            <li onClick={handleLogout} className="logout-btn">
              Logout
            </li>
            {!isEnrolled && (
              <li onClick={handleEnroll} className="enroll">
                Enroll Now
              </li>
            )}
          </>
        )}
      </ul>
    </div>
  );
}

export default SideBar;
