import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Register.css";
import axios from "axios";

const Register = () => {
  const navigate = useNavigate();

  const [values, setValues] = useState({
    username: "",
    email: "",
    otp: "",
    password: "",
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [otpExpired, setOtpExpired] = useState(false);

  const [timer, setTimer] = useState(60);
  const [loading, setLoading] = useState(false);

  const [alert, setAlert] = useState("");
  const [alertType, setAlertType] = useState("");

  /* ======================
     INPUT HANDLER
  ====================== */
  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  /* ======================
     ALERT HELPER (1 sec)
  ====================== */
  const showAlert = (msg, type) => {
    setAlert(msg);
    setAlertType(type);
    setTimeout(() => {
      setAlert("");
      setAlertType("");
    }, 1000);
  };

  /* ======================
     OTP TIMER
  ====================== */
  useEffect(() => {
    let interval;

    if (otpSent && timer > 0 && !otpVerified) {
      interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
    }

    if (timer === 0 && otpSent && !otpVerified) {
      setOtpExpired(true);
    }

    return () => clearInterval(interval);
  }, [otpSent, timer, otpVerified]);

  /* ======================
     REQUEST OTP
  ====================== */
  const requestOtp = async () => {
    if (!values.email) {
      showAlert("Please enter email first", "error");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/v1/send-otp", {
        email: values.email,
      });

      showAlert("OTP sent successfully", "success");
      setOtpSent(true);
      setOtpVerified(false);
      setOtpExpired(false);
      setTimer(60);
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Failed to send OTP",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  /* ======================
     VERIFY OTP
  ====================== */
  const verifyOtp = async () => {
    if (!values.otp) {
      showAlert("Please enter OTP", "error");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/v1/verify-otp", {
        email: values.email,
        otp: values.otp,
      });

      showAlert("OTP verified successfully", "success");
      setOtpVerified(true);
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Invalid OTP",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  /* ======================
     REGISTER USER
  ====================== */
  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);
      const res = await axios.post(
        "http://localhost:3001/api/v1/register",
        {
          username: values.username,
          email: values.email,
          password: values.password,
        }
      );

      localStorage.setItem("token", res.data.token);
      window.dispatchEvent(new Event("authChange"));

      showAlert("Registration successful", "success");

      setTimeout(() => navigate("/"), 1000);
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Registration failed",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="body">
      <div className="Register">
        <Link to="/" className="backBtn">Back</Link>
        <h1>Register</h1>

        {alert && <div className={`alert ${alertType}`}>{alert}</div>}

        <form onSubmit={handleRegister}>
          {/* USERNAME */}
          <input
            type="text"
            name="username"
            placeholder="Username"
            onChange={handleChange}
            disabled={otpVerified}
            required
          />

          {/* EMAIL */}
          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={handleChange}
            disabled={otpSent}
            required
          />

          {/* REQUEST OTP */}
          {!otpSent && (
            <button type="button" onClick={requestOtp} disabled={loading}>
              Request OTP
            </button>
          )}

          {/* OTP INPUT + VERIFY */}
          {otpSent && !otpVerified && (
            <>
              <input
                type="text"
                name="otp"
                placeholder="Enter OTP"
                onChange={handleChange}
              />

              {!otpExpired ? (
                <>
                  <p>OTP expires in {timer}s</p>
                  <button
                    type="button"
                    onClick={verifyOtp}
                    disabled={loading}
                  >
                    Verify OTP
                  </button>
                </>
              ) : (
                <button type="button" onClick={requestOtp}>
                  Resend OTP
                </button>
              )}
            </>
          )}

          {/* PASSWORD + REGISTER */}
          {otpVerified && (
            <>
              <input
                type="password"
                name="password"
                placeholder="Password"
                onChange={handleChange}
                required
              />

              <button type="submit" disabled={loading}>
                Register
              </button>
            </>
          )}
        </form>

        <p>
          Already have an account? <Link to="/Login">Login</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
