import { useState, useEffect } from "react";
import { Outlet } from "react-router-dom";
import Navbar from "./Navbar/Navbar";
import SideBar from "./SideBar/SideBar1";
import Footer from "./Footer/Footer";
import axios from "axios";

function Static() {
  const [isLoggedIn, setIsLoggedIn] = useState(
    !!localStorage.getItem("token")
  );

  const [isEnrolled, setIsEnrolled] = useState(false);
  const [loadingEnroll, setLoadingEnroll] = useState(true);

  // ðŸ”¥ REHYDRATE ENROLL STATE ON REFRESH
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      setIsEnrolled(false);
      return;
    }

    axios
      .get("http://localhost:3001/api/enroll/status", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => {
        setIsEnrolled(res.data.enrolled);
      })
      .catch(() => {
        setIsEnrolled(false);
      })
      .finally(() => {
        setLoadingEnroll(false);
      });
  }, []);

  const logout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setIsEnrolled(false);
  };

  const login = (token) => {
    localStorage.setItem("token", token);
    setIsLoggedIn(true);
  };

  if (loadingEnroll) return null;

  return (
    <div className="layout">
      <Navbar isLoggedIn={isLoggedIn} onLogout={logout} />
      <SideBar
        isLoggedIn={isLoggedIn}
        isEnrolled={isEnrolled}
        onLogout={logout}
      />
      <div className="page-content">
        <Outlet context={{ login, setIsEnrolled }} />
      </div>
      <Footer />
    </div>
  );
}

export default Static;
