import "./SideBar.css";
import logo from "../../assets/logo.png";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../redux/authSlice";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { fetchRegisterStatus } from "../TanStack/fetchRegister";
import axios from "axios";

// function SideBar({ isLoggedIn, onLogout, isEnrolled }) {
function SideBar() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const queryClient = useQueryClient();

  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
  const userType = useSelector((state) => state.auth.user?.userType);

  const { data: isEnrolled, isLoading } = useQuery({
    queryKey: ["enroll-status"],
    queryFn: fetchRegisterStatus,
    enabled: isLoggedIn,
    staleTime: 60 * 1000,
  });

  const handleLogout = async () => {
    await axios.post(
      "http://localhost:3001/api/logout",
      {},
      { withCredentials: true },
    );
    dispatch(logout());
    queryClient.clear();
  };

  return (
    <div className="sidebar">
      <img
        src={logo}
        alt="Logo"
        className="logo-img"
        onClick={() => navigate("/")}
      />
      <h2 className="logo">MyApp</h2>

      <ul className="menu">
        <li onClick={() => navigate("/")}>Home</li>
        <li onClick={() => navigate("/dashboard")}>Dashboard</li>
        <li>Profile</li>
        <li>Settings</li>

        {!isLoggedIn ? (
          <>
            <li onClick={() => navigate("/Login")}>Login</li>
            <li onClick={() => navigate("/SignIn")}>Sign In</li>
          </>
        ) : (
          <>
            <li onClick={handleLogout}>Logout</li>
