import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Register.css";
import axios from "axios";
import { useDispatch } from "react-redux";
import { setUser } from "../redux/authSlice";
import { useQueryClient } from "@tanstack/react-query";

const Register = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const queryClient = useQueryClient();

  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    otp: "",
    userType: "",
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [otpExpired, setOtpExpired] = useState(false);
  const [timer, setTimer] = useState(120);

  const [alert, setAlert] = useState("");
  const [alertType, setAlertType] = useState("");
  const [loading, setLoading] = useState(false);

  /* ---------------- HANDLE CHANGE ---------------- */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  /* ---------------- ALERT ---------------- */
  const showAlert = (msg, type) => {
    setAlert(msg);
    setAlertType(type);
    setTimeout(() => {
      setAlert("");
      setAlertType("");
    }, 1500);
  };

  /* ---------------- OTP TIMER ---------------- */
  useEffect(() => {
    let interval;

    if (otpSent && !otpVerified && timer > 0) {
      interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
    }

    if (timer === 0 && otpSent && !otpVerified) {
      setOtpExpired(true);
    }

    return () => clearInterval(interval);
  }, [otpSent, timer, otpVerified]);

  /* ---------------- REQUEST OTP ---------------- */
  const requestOtp = async () => {
    if (!values.email) {
      showAlert("Please enter email first", "error");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/send-otp", {
        email: values.email,
      });

      showAlert("OTP sent successfully", "success");
      setOtpSent(true);
      setOtpExpired(false);
      setOtpVerified(false);
      setTimer(120);
    } catch (error) {
      showAlert(error.response?.data?.message || "Failed to send OTP", "error");
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- VERIFY OTP ---------------- */
  const verifyOtp = async () => {
    if (!values.otp) {
      showAlert("Please enter OTP", "error");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/verify-otp", {
        email: values.email,
        otp: values.otp,
      });

      showAlert("OTP verified successfully", "success");
      setOtpVerified(true);
    } catch (error) {
      showAlert(error.response?.data?.message || "Invalid OTP", "error");
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- REGISTER ---------------- */
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!otpVerified) {
      showAlert("Please verify OTP first", "error");
      return;
    }

    if (!values.userType) {
      showAlert("Please select a role", "error");
      return;
    }

    try {
      setLoading(true);

      const payload = {
        username: values.username,
        email: values.email,
        password: values.password,
        userType: Number(values.userType),
      };

      const res = await axios.post(
        "http://localhost:3001/api/register",
        payload,
        { withCredentials: true }
      );

      showAlert(res.data.message, "success");

      dispatch(
        setUser({
          username: res.data.user.username,
          userType: res.data.user.userType,
        })
      );

      queryClient.invalidateQueries(["me"]);
      navigate("/dashboard");
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Registration failed",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="body">
      <div className="Register">
        <Link to="/" className="backBtn">Back</Link>
        <h1>Register</h1>

        {alert && <div className={`alert ${alertType}`}>{alert}</div>}

        <form onSubmit={handleSubmit}>
          <label>Username</label>
          <input type="text" name="username" onChange={handleChange} />
          <div>
            <label>Username</label>
            <input
              type="text"
              name="email"
              placeholder="Enter Email"
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Email</label>
            <input
              type="email"
              name="email"
              placeholder="Enter Email"
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>Password</label>
            <input
              type="password"
              name="password"
              placeholder="Enter Password"
              onChange={handleChange}
              required
            />
          </div>
          <label>Select Role</label>
          <select name="userType" onChange={handleChange} className="opt">
            <option value="">Select</option>
            <option value="3">Admin</option>
            <option value="2">Sub-Admin</option>
            <option value="1">User</option>
          </select>

          {otpSent && !otpVerified && (
            <div className="otp">
              <label>Enter OTP</label>
              <input
                type="text"
                name="otp"
                className="otpEnter"
                onChange={handleChange}
              />
              <button type="button" onClick={verifyOtp}>Verify</button>
            </div>
          )}

          {!otpVerified && otpSent && !otpExpired && (
            <div className="otp-timer">
              Time Remaining: {Math.floor(timer / 60)}:
              {String(timer % 60).padStart(2, "0")}
            </div>
          )}

          {!otpVerified && (
            <button
              type="button"
              onClick={requestOtp}
              disabled={loading || (otpSent && !otpExpired)}
            >
              {otpSent ? "Resend OTP" : "Request OTP"}
            </button>
          )}

          <button type="submit" disabled={loading}>
            {loading ? "Registering..." : "Submit"}
          </button>
        </form>

        <p>
          Already have an account? <Link to="/login">Login</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
