import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Register/Register.css";
import axios from "axios";
import { GoogleLogin } from "@react-oauth/google";

const Login = () => {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });
  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };
  const [alert, setAlert] = useState("");
  const [alertType, setAlertType] = useState("");
  const navigate = useNavigate();
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:3001/api/v1/login",
        values
      );
      console.log(response);
      setAlert(response.data.message);
      localStorage.setItem("token", response.data.token);
      window.dispatchEvent(new Event("authChange"));

      setAlertType("success");
      setTimeout(() => {
        setAlert("");
        navigate("/");
      }, 1000);
    } catch (error) {
      setAlert(error.response?.data?.message || "Login failed");
      setAlertType("error");
      setTimeout(() => {
        setAlert("");
      }, 1000);
    }
  };
  const showAlert = (msg, type) => {
    setAlert(msg);
    setAlertType(type);
    setTimeout(() => {
      setAlert("");
      setAlertType("");
    }, 1000);
  };
  const handleGoogleLogin =async (credentialR)
  return (
    <div className="body">
      <div className="Register">
        <Link to="/" type="back" className="backBtn">
          Back
        </Link>
        <h1>Login</h1>
        {alert && <div className={`alert ${alertType}`}>{alert}</div>}
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              placeholder="Enter Email"
              name="email"
              onChange={handleChange}
            />
          </div>

          <div>
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              placeholder="Enter Password"
              name="password"
              onChange={handleChange}
            />
          </div>

          <button type="submit">Login</button>
        </form>
        <div style={{ margin: "15px 0", textAlign: "center" }}>
          <span>OR</span>
        </div>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <GoogleLogin
            onSuccess={handleGoogleLogin}
            onError={() => showAlert("Google Login Failed", "error")}
          />
        </div>
        <div>
          <p>Don't have an account?</p>
          <Link to="/Register">Register</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
