import React, {useState} from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../Register/Register.css';
import axios from 'axios';

const Login = () => {
      const [values ,setValues]=useState({
          email: '',
          password: ''
      })
      const handleChange= (e) => {
        setValues({...values, [e.target.name]:e.target.value})
    }
    const [alert, setAlert] = useState('');
    const navigate=useNavigate();
    const handleSubmit = async (e) => {
        e.preventDefault();
        try{
            const response=await axios.post('http://localhost:3001/auth/login',values)
            console.log(response)
            setAlert(response.data.message);
            setTimeout(() => {
              setAlert('');
              navigate('/');
            }, 1000);
        }
        catch(error){
            setAlert(error.response?.data?.message || 'Login failed');
            setTimeout(() => {
              setAlert('');
            }, 1000);
        }
    }

  return (
    <div className='body'>
      <div className='Register'>
        <Link to="/" type="back" className='backBtn'>Back</Link>
        <h1>Login</h1>
        {alert && <div className="alert">{alert}</div>}
        <form>
          <div>
            <label htmlFor="email">Email</label>
            <input id="email" type="email" placeholder="Enter Email" name='email' onChange={handleChange} />
          </div>

          <div>
            <label htmlFor="password" >Password</label>
            <input id="password" type="password" placeholder="Enter Password" name='password' onChange={handleChange} />
          </div>

          <button type="submit" onClick={handleSubmit}>Login</button>
        </form>

        <div>
          <p>Don't have an account?</p>
          <Link to="/Register">Register</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
