import { Outlet } from "react-router-dom";
import Navbar from "./Navbar/Navbar";
import SideBar from "./SideBar/SideBar1";
import Footer from "./Footer/Footer";
import { useDispatch, useSelector } from "react-redux";
import { useQuery } from "@tanstack/react-query";
import { setUser, logout, authChecked } from "./redux/authSlice";
import { fetchMe } from "./TanStack/userDetail";

function Static() {
  const dispatch = useDispatch();
  const initialized = useSelector((state) => state.auth.initialized);

  useQuery({
    queryKey: ["me"],
    queryFn: fetchMe,
    enabled: !initialized, // âœ… run once
    retry: false,
    onSuccess: (data) => {
      dispatch(setUser(data)); // logged in
    },
    onError: () => {
      dispatch(logout());      // guest
    },
    onSettled: () => {
      dispatch(authChecked()); // ðŸ”¥ AUTH CHECK FINISHED (KEY FIX)
    },
  });

  if (!initialized) return null; // prevent flicker

  return (
    <div className="layout">
      <Navbar />
      <SideBar />
      <div className="page-content">
        <Outlet />
      </div>
      <Footer />
    </div>
  );
}

export default Static;
