import { useState, useEffect } from "react";
import { Outlet } from "react-router-dom";
import Navbar from "./Navbar/Navbar";
import SideBar from "./SideBar/SideBar1";
import Footer from "./Footer/Footer";
import axios from "axios";
import {useDispatch , useSelector} from "react-redux";
import { fetchEnrollStatus } from "./redux/enrollSlice";

function Static() {
  // const [isLoggedIn, setIsLoggedIn] = useState(
  //   !!localStorage.getItem("token")
  // );

  const [isEnrolled, setIsEnrolled] = useState(false);
  // useEffect(() => {
  //   const token = localStorage.getItem("token");

  //   if (!token) {
  //     setIsEnrolled(false);
  //     return;
  //   }

  //   axios
  //     .get("http://localhost:3001/api/enroll/status", {
  //       headers: {
  //         Authorization: `Bearer ${token}`,
  //       },
  //     })
  //     .then((res) => {
  //       setIsEnrolled(res.data.enrolled);
  //     })
  //     .catch(() => {
  //       setIsEnrolled(false);
  //     })
  // }, []);

  const logout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setIsEnrolled(false);
  };

  const login = (token) => {
    localStorage.setItem("token", token);
    setIsLoggedIn(true);
  };

  const dispatch=useDispatch();
  const enrollStatus=useSelector((state) => state.enroll.status);
  
  useEffect(() => {
    dispatch(fetchEnrollStatus());
  }, [dispatch]);

  if (enrollStatus === "loading") return null;
  return (
    <div className="layout">
      {/* <Navbar isLoggedIn={isLoggedIn} onLogout={logout} />
      <SideBar
        isLoggedIn={isLoggedIn}
        isEnrolled={isEnrolled}
        onLogout={logout}
      /> */}
      <Navbar />
      <div className="page-content">
        <Outlet context={{ login }} />
      </div>
      <Footer />
    </div>
  );
}

export default Static;
