import React, { useState } from 'react'
import {Link , useNavigate} from 'react-router-dom'
import "./Register.css"
import axios from 'axios'

const Register = () => {
    const [values ,setValues]=useState({
        username: '',
        email: '',
        password: ''
    })
    const navigate=useNavigate();
    const [alert, setAlert] = useState('');
    const [alertType,setAlertType]=useState('');
    const [otpsend, setOtpSend] = useState(false);

    const handleChanges= (e) => {
        setValues({...values, [e.target.name]:e.target.value})
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        try{
            const response=await axios.post('http://localhost:3001/api/v1/register',values)
            console.log(response)
            setAlert(response.data.message);
            setAlertType("success");
            localStorage.setItem("token", response.data.token);
            window.dispatchEvent(new Event("authChange"));
            setTimeout(() => {
              setAlert('');
              navigate('/');
            }, 1000);
        }
        catch(error){
            console.log(error);
            setAlert(error.response?.data?.message || 'Login failed');
            setAlertType("error");
            setTimeout(() => {
              setAlert('');
            }, 1000);
        }
    }
    const requestOtp = async () => {
        await axios.post("http://localhost:3001/api/v1/send-otp", {
            email: values.email,
        });
        setOtpSend(true);
    };

  return (
    <div className='body'>
        <div className='Register'>
        <div >
            <Link to="/" type="back" className='backBtn'>Back</Link>
            <h1>Register</h1>
            {alert  && <div className={`alert ${alertType}`}>{alert}</div>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor='username'>Username</label>
                    <input type='text' placeholder='Enter Username' name="username" onChange={handleChanges} />
                </div>
                <div>
                    <label htmlFor='email'>Email</label>
                    <input type='email' placeholder='Enter Email' name="email" onChange={handleChanges} />
                </div>
                {!otpsend && (
                        <button>Request OTP</button>
                )}
                <div className='otp'>
                    
                    <label htmlFor='otp'>Enter OTP:</label>
                    <input type='otp' placeholder='Enter Otp' name="otp" className='otpEnter' onChange={handleChanges} />
                </div>
                <div>
                        <label htmlFor='password'>Password</label>
                        <input type='password' placeholder='Enter Password' name="password" onChange={handleChanges} />
                    </div>
                
                <button>Submit</button>
            </form>
            <div>
                <p>Already have account</p>
                <Link to="/Login">Login</Link>
            </div>
        </div>
        </div>
    </div>
  )
}

export default Register
