import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Register.css";
import axios from "axios";

const Register = () => {
  const navigate = useNavigate();

  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    otp: "",
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [otpExpired, setOtpExpired] = useState(false);
  const [timer, setTimer] = useState(120);

  const [alert, setAlert] = useState("");
  const [alertType, setAlertType] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const showAlert = (msg, type) => {
    setAlert(msg);
    setAlertType(type);
    setTimeout(() => {
      setAlert("");
      setAlertType("");
    }, 1000);
  };

  useEffect(() => {
    let interval;

    if (otpSent && !otpVerified && timer > 0) {
      interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
    }

    if (timer === 0 && otpSent && !otpVerified) {
      setOtpExpired(true);
    }

    if (otpVerified) {
    clearInterval(interval);
  }
    return () => clearInterval(interval);
  }, [otpSent, timer, otpVerified]);

  const requestOtp = async () => {
    if (!values.email) {
      showAlert("Please enter email first", "error");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/send-otp", {
        email: values.email,
      });

      showAlert("OTP sent successfully", "success");
      setOtpSent(true);
      setOtpExpired(false);
      setOtpVerified(false);
      setTimer(120);
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Failed to send OTP",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  const verifyOtp = async () => {
    if (!values.otp) {
      showAlert("Please enter OTP", "error");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/verify-otp", {
        email: values.email,
        otp: values.otp,
      });

      showAlert("OTP verified successfully", "success");
      setOtpVerified(true);
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Invalid OTP",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!otpVerified) {
      showAlert("Please verify OTP first", "error");
      return;
    }

    try {
      setLoading(true);
      const res = await axios.post(
        "http://localhost:3001/api/register",
        {
          username: values.username,
          email: values.email,
          password: values.password,
        }
      );

      localStorage.setItem("token", res.data.token);
      window.dispatchEvent(new Event("authChange"));

      showAlert("Registration successful", "success");

      setTimeout(() => navigate("/"), 1000);
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Registration failed",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="body">
      <div className="Register">
        <div>
          <Link to="/" type="back" className="backBtn">Back</Link>
          <h1>Register</h1>

          {alert && <div className={`alert ${alertType}`}>{alert}</div>}

          <form onSubmit={handleSubmit}>
            <div>
              <label>Username</label>
              <input
                type="text"
                name="username"
                onChange={handleChange}
              />
            </div>

            <div>
              <label>Email</label>
              <input
                type="email"
                name="email"
                onChange={handleChange}
                disabled={otpSent}
              />
            </div>

            {otpVerified && (<div>
              <label>Password</label>
              <input
                type="password"
                name="password"
                onChange={handleChange}
                disabled={!otpVerified}
              />
            </div>)}

            {otpSent && !otpVerified && (
              <div className="otp">
                <label>Enter OTP:</label>
                <input
                  type="text"
                  name="otp"
                  className="otpEnter"
                  onChange={handleChange}
                />
                <div  className="verify">
                  <button type="button" onClick={verifyOtp}>
                    Verify
                  </button>
                </div>
              </div>
            )}
            <button type="button" onClick={requestOtp} disabled={loading || (otpSent && !otpExpired)}>
                {otpSent ? "Resend OTP":"Request OTP"}
            </button>
            <button type="submit">Submit</button>
          </form>

          <div>
            <p>Already have account</p>
            <Link to="/Login">Login</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
