import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./SignIn.css";
import axios from "axios";
import { useDispatch } from "react-redux";
import { setUser } from "../redux/authSlice";
import { useQueryClient } from "@tanstack/react-query";
import { Dumbbell } from "lucide-react";

const SignIn = () => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?#&])[A-Za-z\d@$!%*?#&]{8,}$/;


  const navigate = useNavigate();
  const dispatch = useDispatch();
  const queryClient = useQueryClient();

  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    otp: "",
    userType: "",
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [otpExpired, setOtpExpired] = useState(false);
  const [timer, setTimer] = useState(120);

  const [alert, setAlert] = useState("");
  const [alertType, setAlertType] = useState("");
  const [loading, setLoading] = useState(false);

  /* ---------------- HANDLE CHANGE ---------------- */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
  };

  /* ---------------- ALERT ---------------- */
  const showAlert = (msg, type) => {
    setAlert(msg);
    setAlertType(type);
    setTimeout(() => {
      setAlert("");
      setAlertType("");
    }, 1500);
  };

  /* ---------------- OTP TIMER ---------------- */
  useEffect(() => {
    let interval;

    if (otpSent && !otpVerified && timer > 0) {
      interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
    }

    if (timer === 0 && otpSent && !otpVerified) {
      setOtpExpired(true);
    }

    return () => clearInterval(interval);
  }, [otpSent, timer, otpVerified]);

  /* ---------------- REQUEST OTP ---------------- */
  const requestOtp = async () => {
    if (!values.email) {
      showAlert("Please enter email first", "error");
      return;
    }
    if (values.email) {
      if (!emailRegex.test(values.email)) {
        showAlert("Please enter a valid email address","error");
        return;
      }
    }

    if (values.password) {
      if (!passwordRegex.test(values.password)) {
        showAlert("Password must be at least 8 characters and include uppercase, lowercase, number, and special character","error");
        return;
      }
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/send-otp", {
        email: values.email,
      });

      showAlert("OTP sent successfully", "success");
      setOtpSent(true);
      setOtpExpired(false);
      setOtpVerified(false);
      setTimer(120);
    } catch (error) {
      showAlert(error.response?.data?.message || "Failed to send OTP", "error");
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- VERIFY OTP ---------------- */
  const verifyOtp = async () => {
    if (!values.otp) {
      showAlert("Please enter OTP", "error");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:3001/api/verify-otp", {
        email: values.email,
        otp: values.otp,
      });

      showAlert("OTP verified successfully", "success");
      setOtpVerified(true);
    } catch (error) {
      showAlert(error.response?.data?.message || "Invalid OTP", "error");
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- REGISTER ---------------- */
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!otpVerified) {
      showAlert("Please verify OTP first", "error");
      return;
    }

    if (!values.userType) {
      showAlert("Please select a role", "error");
      return;
    }

    try {
      setLoading(true);

      const payload = {
        username: values.username,
        email: values.email,
        password: values.password,
        userType: Number(values.userType),
      };

      const res = await axios.post(
        "http://localhost:3001/api/signIn",
        payload,
        { withCredentials: true },
      );

      showAlert(res.data.message, "success");

      dispatch(
        setUser({
          username: res.data.user.username,
          userType: res.data.user.userType,
        }),
      );

      queryClient.invalidateQueries(["me"]);
      navigate("/");
    } catch (error) {
      showAlert(
        error.response?.data?.message || "Registration failed",
        "error",
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      {/* ground */}
      <div className="login-bg">
        <div className="login-bg-decoration-1" />
        <div className="login-bg-decoration-2" />
      </div>

      <div className="login-container">
        {/* Card */}
        <div className="login-card">
          <div className="login-back">
            <Link to="/">← Back to home</Link>
          </div>
          <div className="login-card-header">
            <Link to="/" className="login-logo-link">
              <Dumbbell className="login-logo-icon" />
              <span className="login-logo-text">FitTrack Login</span>
            </Link>
          </div>
          {alert && <div className={`alert ${alertType}`}>{alert}</div>}

          <div className="login-card-content">
            <form onSubmit={handleSubmit} className="login-form">
              <div className="form-group">
                <label>Username</label>
                <input
                  type="name"
                  name="name"
                  placeholder="Username"
                  value={values.email}
                  onChange={handleChange}
                  required
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  placeholder="name@example.com"
                  value={values.email}
                  onChange={handleChange}
                  required
                  className="form-input"
                />
              </div>

              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  placeholder="••••••••"
                  value={values.password}
                  onChange={handleChange}
                  required
                  className="form-input"
                />
              </div>

              <div className="login-forgot">
                <Link to="/resetPassword">Forgot password?</Link>
              </div>

              <button type="submit" className="login-submit-btn">
                Sign In
              </button>
            </form>

            {/* OR */}
            <div className="login-divider">OR</div>


            <div className="login-signup">
              <p>
                Don't have an account?{" "}
                <Link to="/signin" className="login-signup-link">
                  Sign up
                </Link>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignIn;
