import "./SideBar.css";
import logo from "../../assets/logo.png";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../redux/authSlice";
import {useQuery, useQueryClient} from "@tanstack/react-query"
import { fetchEnrollStatus } from "../TanStack/fetchEnroll";
import axios from "axios";

// function SideBar({ isLoggedIn, onLogout, isEnrolled }) {
function SideBar() {
  const navigate = useNavigate();
  const dispatch=useDispatch();
  const queryClient = useQueryClient();

  const isLoggedIn= useSelector((state) => state.auth.isLoggedIn)
  // const isEnrolled = useSelector((state) => state.enroll.isEnrolled);

  const{
    data:isEnrolled,
    isLoading,
  } = useQuery({
    queryKey: ["enroll-status"],
    queryFn: fetchEnrollStatus,
    enabled: isLoggedIn,
    staleTime: 60*1000,
  });

  const handleLogout = async() => {
    await axios.post(
      "http://localhost:3001/api/logout",
      {},
      { withCredentials: true }
    );
    dispatch(logout());
    queryClient.setQueryData(["me"], null);
  queryClient.invalidateQueries(["enroll-status"]);
  };

  return (
    <div className="sidebar">
      <img src={logo} alt="Logo" className="logo-img" />
      <h2 className="logo">MyApp</h2>

      <ul className="menu">
        <li onClick={() => navigate("/")}>Dashboard</li>
        <li>Profile</li>
        <li>Settings</li>

        {!isLoggedIn ? (
          <>
            <li onClick={() => navigate("/Login")}>Login</li>
            <li onClick={() => navigate("/Register")}>Register</li>
          </>
        ) : (
          <>
            <li onClick={handleLogout}>Logout</li>
            {!isLoading && !isEnrolled && (
              <li onClick={() => navigate("/Enroll")}>
                Enroll Now
              </li>
            )}
          </>
        )}
      </ul>
    </div>
  );
}

export default SideBar;
